// src/pages/Feed.js
import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import CreatePost from "../components/CreatePost";
import axios from "axios";

function Feed() {
  const [posts, setPosts] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) return;

    // Decode or fetch user details (assuming your backend returns it after login)
    const userData = JSON.parse(localStorage.getItem("user"));
    setUser(userData);

    // Fetch posts
    axios
      .get("http://localhost:5000/api/posts")
      .then((res) => setPosts(res.data))
      .catch((err) => console.error(err));
  }, []);

  return (
    <div>
      <Navbar user={user} setUser={setUser} />
      <div className="max-w-2xl mx-auto mt-6">
        <CreatePost user={user} setPosts={setPosts} />
        <div className="mt-6 space-y-4">
          {posts
            .slice()
            .reverse()
            .map((post, idx) => (
              <div
                key={idx}
                className="border border-gray-300 p-4 rounded-lg shadow-sm bg-white"
              >
                <h2 className="font-semibold">{post.user?.name}</h2>
                <p className="text-gray-700 mt-2">{post.text}</p>
                <p className="text-sm text-gray-500 mt-1">
                  {new Date(post.createdAt).toLocaleString()}
                </p>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
}

export default Feed;
