// src/components/Navbar.js
import React from "react";
import { useNavigate } from "react-router-dom";

function Navbar({ user, setUser }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    setUser(null);
    navigate("/");
  };

  return (
    <nav className="bg-blue-600 text-white flex justify-between items-center p-4">
      <h1 className="text-xl font-bold cursor-pointer" onClick={() => navigate("/feed")}>
        LinkedIn Clone
      </h1>

      <div className="flex items-center gap-4">
        <span className="font-medium">
          {user ? `Hello, ${user.name || user.email}` : ""}
        </span>
        <button
          onClick={handleLogout}
          className="bg-white text-blue-600 px-3 py-1 rounded-md font-semibold hover:bg-gray-100"
        >
          Logout
        </button>
      </div>
    </nav>
  );
}

export default Navbar;
